import React from "react";
import ActivityTracker from "./ActivityTracker";
import YogaMeditation from "./YogaMeditation";
import AppointmentScheduler from "./AppointmentScheduler";
import ChatWithTherapist from "./ChatWithTherapist";
import MedicationReminder from "./MedicationReminder";
import ProgressDashboard from "./ProgressDashboard";
import DailyJournal from "./DailyJournal";
import EducationalResources from "./EducationalResources";

const Dashboard = () => {
  return (
    <div
      className="container-fluid py-4"
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1603209011909-2c84b55cb8b4?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=1920')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundAttachment: "fixed",
        filter: "blur(5px)",
        position: "relative",
      }}
    >
      <div
        style={{
          position: "absolute",
          inset: 0,
          backgroundColor: "rgba(255, 255, 255, 0.7)",
          backdropFilter: "blur(5px)",
          zIndex: 1,
        }}
      >
        <h2 className="mb-4 text-center fw-bold" style={{ zIndex: 2, position: "relative" }}>
          Patient Dashboard
        </h2>
        <div className="row g-4">
          <div className="col-lg-4 col-md-6">
            <ActivityTracker />
          </div>
          <div className="col-lg-4 col-md-6">
            <YogaMeditation />
          </div>
          <div className="col-lg-4 col-md-6">
            <AppointmentScheduler />
          </div>
          <div className="col-lg-4 col-md-6">
            <ChatWithTherapist />
          </div>
          <div className="col-lg-4 col-md-6">
            <MedicationReminder />
          </div>
          <div className="col-lg-4 col-md-6">
            <ProgressDashboard />
          </div>
          <div className="col-lg-4 col-md-6">
            <DailyJournal />
          </div>
          <div className="col-lg-4 col-md-6">
            <EducationalResources />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
