import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend } from "chart.js";

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

const ActivityTracker = () => {
  const [timeRange, setTimeRange] = useState("daily");

  const getData = () => {
    switch (timeRange) {
      case "weekly":
        return {
          labels: ["Week 1", "Week 2", "Week 3", "Week 4"],
          datasets: [
            { label: "Mood", data: [7, 6, 8, 7], borderColor: "#243642", fill: false },
            { label: "Sleep (Hours)", data: [7.5, 8, 7, 6.5], borderColor: "#387478", fill: false },
          ],
        };
      case "monthly":
        return {
          labels: ["Jan", "Feb", "Mar", "Apr"],
          datasets: [
            { label: "Mood", data: [7, 6.5, 8, 7.5], borderColor: "#243642", fill: false },
            { label: "Sleep (Hours)", data: [7.5, 8, 7.2, 6.8], borderColor: "#387478", fill: false },
          ],
        };
      default:
        return {
          labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
          datasets: [
            { label: "Mood", data: [6, 7, 8, 5, 9, 7, 8], borderColor: "#243642", fill: false },
            { label: "Sleep (Hours)", data: [7, 8, 6, 8, 9, 7, 6], borderColor: "#387478", fill: false },
          ],
        };
    }
  };

  return (
    <div
      className="d-flex flex-column justify-content-between p-4 shadow-lg rounded"
      style={{
        background: "linear-gradient(135deg, #243642, #387478, #629584, #E2F1E7)",
        height: "300px",
        width: "100%",
        color: "#fff",
      }}
    >
      <h5 className="fw-bold">Activity Tracker</h5>
      <div className="btn-group mb-3">
        <button
          className={`btn ${timeRange === "daily" ? "btn-light" : "btn-outline-light"} me-2`}
          onClick={() => setTimeRange("daily")}
        >
          Daily
        </button>
        <button
          className={`btn ${timeRange === "weekly" ? "btn-light" : "btn-outline-light"} me-2`}
          onClick={() => setTimeRange("weekly")}
        >
          Weekly
        </button>
        <button
          className={`btn ${timeRange === "monthly" ? "btn-light" : "btn-outline-light"}`}
          onClick={() => setTimeRange("monthly")}
        >
          Monthly
        </button>
      </div>
      <Line data={getData()} />
    </div>
  );
};

export default ActivityTracker;
