import React from "react";
import ActivityTracker from "./ActivityTracker";
import YogaMeditation from "./YogaMeditation";
import AppointmentScheduler from "./AppointmentScheduler";
import ChatWithTherapist from "./ChatWithTherapist";
import MedicationReminder from "./MedicationReminder";
import ProgressDashboard from "./ProgressDashboard";
import DailyJournal from "./DailyJournal";
import EducationalResources from "./EducationalResources";

const Dashboard = () => {
  const sectionStyle = {
    borderRadius: "10px",
    padding: "15px",
    color: "#fff",
    height: "100%",
    backgroundSize: "cover",
    backgroundPosition: "center",
  };

  return (
    <div className="container-fluid py-4">
      <h2 className="mb-4 text-center fw-bold">Patient Dashboard</h2>
      <div className="row g-3">
        {/* Activity Tracker */}
        <div
          className="col-lg-4 col-md-6"
          style={{
            ...sectionStyle,
            backgroundImage:
              "url('https://images.unsplash.com/photo-1517694712202-14dd9538aa97?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=400')",
          }}
        >
          <ActivityTracker />
        </div>

        {/* Yoga and Meditation */}
        <div
          className="col-lg-4 col-md-6"
          style={{
            ...sectionStyle,
            backgroundImage:
              "url('https://images.unsplash.com/photo-1574706472533-bf94c78c2e4b?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=400')",
          }}
        >
          <YogaMeditation />
        </div>

        {/* Appointment Scheduler */}
        <div
          className="col-lg-4 col-md-6"
          style={{
            ...sectionStyle,
            backgroundImage:
              "url('https://images.unsplash.com/photo-1551836022-d5d88e9218df?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=400')",
          }}
        >
          <AppointmentScheduler />
        </div>

        {/* Chat with Therapist */}
        <div
          className="col-lg-4 col-md-6"
          style={{
            ...sectionStyle,
            backgroundImage:
              "url('https://images.unsplash.com/photo-1524253482453-3fed8d2fe12b?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=400')",
          }}
        >
          <ChatWithTherapist />
        </div>

        {/* Medication Reminder */}
        <div
          className="col-lg-4 col-md-6"
          style={{
            ...sectionStyle,
            backgroundImage:
              "url('https://images.unsplash.com/photo-1597764696983-9b5123b4bc9c?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=400')",
          }}
        >
          <MedicationReminder />
        </div>

        {/* Progress Dashboard */}
        <div
          className="col-lg-4 col-md-6"
          style={{
            ...sectionStyle,
            backgroundImage:
              "url('https://images.unsplash.com/photo-1508230876213-d0cbfdcb027f?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=400')",
          }}
        >
          <ProgressDashboard />
        </div>

        {/* Daily Journal */}
        <div
          className="col-lg-4 col-md-6"
          style={{
            ...sectionStyle,
            backgroundImage:
              "url('https://images.unsplash.com/photo-1515876305429-6c94d543446d?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=400')",
          }}
        >
          <DailyJournal />
        </div>

        {/* Educational Resources */}
        <div
          className="col-lg-4 col-md-6"
          style={{
            ...sectionStyle,
            backgroundImage:
              "url('https://images.unsplash.com/photo-1516919549054-bb2ff3d2cfe4?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=400')",
          }}
        >
          <EducationalResources />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
