import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap";
import { FaCalendarAlt, FaClock, FaUserMd } from "react-icons/fa";
import { Pagination } from "react-bootstrap";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";

const MyAppointments = () => {
  const [appointments, setAppointments] = useState([
    { id: 1, date: "2024-12-05", time: "10:00 AM", doctor: "Dr. Smith", status: "Confirmed" },
    { id: 2, date: "2024-12-10", time: "02:00 PM", doctor: "Dr. Johnson", status: "Pending" },
    // Add more appointments for pagination
  ]);

  const [currentPage, setCurrentPage] = useState(1);
  const [appointmentsPerPage] = useState(5);
  const [searchTerm, setSearchTerm] = useState("");
  const [filterStatus, setFilterStatus] = useState("");
  const [showDetails, setShowDetails] = useState(false);
  const [currentAppointment, setCurrentAppointment] = useState(null);

  const handleShowDetails = (appointment) => {
    setCurrentAppointment(appointment);
    setShowDetails(true);
  };

  const handleCloseDetails = () => setShowDetails(false);

  const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

  // Filter and Search Logic
  const filteredAppointments = appointments.filter((appt) => {
    return (
      (searchTerm === "" ||
        appt.doctor.toLowerCase().includes(searchTerm.toLowerCase()) ||
        appt.date.includes(searchTerm)) &&
      (filterStatus === "" || appt.status === filterStatus)
    );
  });

  // Pagination Logic
  const indexOfLastAppointment = currentPage * appointmentsPerPage;
  const indexOfFirstAppointment = indexOfLastAppointment - appointmentsPerPage;
  const currentAppointments = filteredAppointments.slice(
    indexOfFirstAppointment,
    indexOfLastAppointment
  );

  // Total Pages
  const totalPages = Math.ceil(filteredAppointments.length / appointmentsPerPage);

  return (
    <div
      style={{
        backgroundColor: "#E2F1E7",
        minHeight: "100vh",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <h2
        className="fw-bold text-center mb-4"
        style={{
          color: "#243642",
          fontSize: "2rem",
        }}
      >
        My Appointments
      </h2>

      {/* Search and Filter */}
      <div className="mb-4 d-flex justify-content-center align-items-center gap-3">
        <input
          type="text"
          className="form-control"
          style={{ maxWidth: "300px" }}
          placeholder="Search by doctor or date"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select
          className="form-select"
          style={{ maxWidth: "200px" }}
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
        >
          <option value="">All Status</option>
          <option value="Confirmed">Confirmed</option>
          <option value="Pending">Pending</option>
        </select>
      </div>

      {/* Appointment List */}
      <div
        className="rounded shadow p-4 mb-4"
        style={{
          backgroundColor: "#387478",
          color: "#E2F1E7",
          width: "100%",
          maxWidth: "800px",
        }}
      >
        <h4 className="mb-3">Appointments</h4>
        {currentAppointments.length > 0 ? (
          <ul className="list-group">
            {currentAppointments.map((appt) => (
              <li
                key={appt.id}
                className="mb-3 rounded d-flex justify-content-between align-items-center p-3"
                style={{
                  backgroundColor: "#243642",
                  color: "#E2F1E7",
                  boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
                  borderRadius: "10px",
                  cursor: "pointer",
                  transition: "transform 0.2s",
                }}
                onClick={() => handleShowDetails(appt)}
              >
                <div>
                  <FaCalendarAlt className="me-2" /> {appt.date} <br />
                  <FaClock className="me-2" /> {appt.time} <br />
                  <FaUserMd className="me-2" /> {appt.doctor} <br />
                  <strong>Status:</strong>{" "}
                  <span
                    className={`badge ${
                      appt.status === "Confirmed"
                        ? "bg-success"
                        : appt.status === "Pending"
                        ? "bg-warning text-dark"
                        : "bg-secondary"
                    }`}
                  >
                    {appt.status}
                  </span>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p>No appointments match your criteria.</p>
        )}
      </div>

      {/* Pagination */}
      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => handlePageChange(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>

      {/* Calendar View */}
      <div
        className="rounded shadow p-4 mt-4"
        style={{
          backgroundColor: "#629584",
          color: "#243642",
          width: "100%",
          maxWidth: "800px",
        }}
      >
        <h4 className="mb-3">Calendar View</h4>
        <FullCalendar
          plugins={[dayGridPlugin]}
          initialView="dayGridMonth"
          events={appointments.map((appt) => ({
            title: appt.doctor,
            date: appt.date,
          }))}
        />
      </div>

      {/* Details Modal */}
      <Modal show={showDetails} onHide={handleCloseDetails}>
        <Modal.Header closeButton>
          <Modal.Title>Appointment Details</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>
            <strong>Date:</strong> {currentAppointment?.date}
          </p>
          <p>
            <strong>Time:</strong> {currentAppointment?.time}
          </p>
          <p>
            <strong>Doctor:</strong> {currentAppointment?.doctor}
          </p>
          <p>
            <strong>Status:</strong>{" "}
            <span
              className={`badge ${
                currentAppointment?.status === "Confirmed"
                  ? "bg-success"
                  : currentAppointment?.status === "Pending"
                  ? "bg-warning text-dark"
                  : "bg-secondary"
              }`}
            >
              {currentAppointment?.status}
            </span>
          </p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseDetails}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default MyAppointments;
