import React, { useState } from "react";
import Calendar from "react-calendar"; // Use react-calendar library
import "react-calendar/dist/Calendar.css"; // Default styles for react-calendar
import { FaCalendarAlt, FaClock, FaUserMd } from "react-icons/fa";

const MyAppointments = () => {
  const [appointments, setAppointments] = useState([
    { id: 1, date: "2024-12-05", time: "10:00 AM", doctor: "Dr. Smith", status: "Confirmed" },
    { id: 2, date: "2024-12-10", time: "02:00 PM", doctor: "Dr. Johnson", status: "Pending" },
    { id: 3, date: "2024-12-15", time: "01:00 PM", doctor: "Dr. Adams", status: "Completed" },
  ]);

  const [search, setSearch] = useState("");

  // Filtered appointments based on search
  const filteredAppointments = appointments.filter(
    (appt) =>
      appt.doctor.toLowerCase().includes(search.toLowerCase()) ||
      appt.date.includes(search) ||
      appt.status.toLowerCase().includes(search.toLowerCase())
  );

  // Highlight dates with appointments
  const highlightDates = appointments.map((appt) => new Date(appt.date).toDateString());

  return (
    <div
      style={{
        backgroundColor: "#E2F1E7",
        minHeight: "100vh",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <h2
        className="fw-bold text-center mb-4"
        style={{
          color: "#243642",
          fontSize: "2rem",
        }}
      >
        My Appointments
      </h2>

      {/* Search Bar */}
      <div className="mb-4" style={{ maxWidth: "800px", margin: "0 auto" }}>
        <input
          type="text"
          className="form-control"
          placeholder="Search by doctor, date, or status"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      {/* Layout for Current Appointments and Calendar */}
      <div className="d-flex" style={{ flexGrow: 1 }}>
        {/* Current Appointments */}
        <div
          className="rounded shadow p-4"
          style={{
            flex: "2",
            backgroundColor: "#387478",
            color: "#E2F1E7",
            marginRight: "20px",
            borderRadius: "10px",
            overflowY: "auto",
          }}
        >
          <h4 className="mb-3">Current Appointments</h4>
          {filteredAppointments.length > 0 ? (
            <ul className="list-group">
              {filteredAppointments.map((appt) => (
                <li
                  key={appt.id}
                  className="mb-3 rounded d-flex justify-content-between align-items-center p-3"
                  style={{
                    backgroundColor: "#243642",
                    color: "#E2F1E7",
                    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
                    borderRadius: "10px",
                  }}
                >
                  <div>
                    <FaCalendarAlt className="me-2" />
                    <strong>Date:</strong> {appt.date} <br />
                    <FaClock className="me-2" />
                    <strong>Time:</strong> {appt.time} <br />
                    <FaUserMd className="me-2" />
                    <strong>Doctor:</strong> {appt.doctor} <br />
                    <strong>Status:</strong>{" "}
                    <span
                      className={`badge ${
                        appt.status === "Confirmed"
                          ? "bg-success"
                          : appt.status === "Pending"
                          ? "bg-warning text-dark"
                          : "bg-secondary"
                      }`}
                    >
                      {appt.status}
                    </span>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>No appointments found.</p>
          )}
        </div>

        {/* Calendar */}
        <div
          className="rounded shadow p-4"
          style={{
            flex: "1",
            backgroundColor: "#FFFFFF",
            border: "1px solid #629584",
            borderRadius: "10px",
            boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Calendar
            tileClassName={({ date }) =>
              highlightDates.includes(date.toDateString()) ? "highlight" : null
            }
            style={{ borderRadius: "10px" }}
          />
        </div>
      </div>
    </div>
  );
};

export default MyAppointments;
