import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend } from "chart.js";

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

const ActivityTracker = () => {
  const [timeRange, setTimeRange] = useState("daily");

  const getData = () => {
    switch (timeRange) {
      case "weekly":
        return {
          labels: ["Week 1", "Week 2", "Week 3", "Week 4"],
          datasets: [
            { label: "Mood", data: [7, 6, 8, 7], borderColor: "#4A90E2", backgroundColor: "#4A90E2" },
            { label: "Sleep (Hours)", data: [7.5, 8, 7, 6.5], borderColor: "#50E3C2", backgroundColor: "#50E3C2" },
          ],
        };
      case "monthly":
        return {
          labels: ["Jan", "Feb", "Mar", "Apr"],
          datasets: [
            { label: "Mood", data: [7, 6.5, 8, 7.5], borderColor: "#4A90E2", backgroundColor: "#4A90E2" },
            { label: "Sleep (Hours)", data: [7.5, 8, 7.2, 6.8], borderColor: "#50E3C2", backgroundColor: "#50E3C2" },
          ],
        };
      default:
        return {
          labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
          datasets: [
            { label: "Mood", data: [6, 7, 8, 5, 9, 7, 8], borderColor: "#4A90E2", backgroundColor: "#4A90E2" },
            { label: "Sleep (Hours)", data: [7, 8, 6, 8, 9, 7, 6], borderColor: "#50E3C2", backgroundColor: "#50E3C2" },
          ],
        };
    }
  };

  return (
    <div
      className="p-4 text-white"
      style={{
        background: "linear-gradient(120deg, #4A90E2, #50E3C2)",
        borderRadius: "12px",
        boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
      }}
    >
      <h5 className="mb-4 fw-bold">Activity Tracker</h5>
      <div className="btn-group mb-3">
        <button
          className="btn btn-light"
          onClick={() => setTimeRange("daily")}
          style={{ borderRadius: "20px" }}
        >
          Daily
        </button>
        <button
          className="btn btn-light"
          onClick={() => setTimeRange("weekly")}
          style={{ borderRadius: "20px" }}
        >
          Weekly
        </button>
        <button
          className="btn btn-light"
          onClick={() => setTimeRange("monthly")}
          style={{ borderRadius: "20px" }}
        >
          Monthly
        </button>
      </div>
      <Line data={getData()} />
    </div>
  );
};

export default ActivityTracker;
